CREATE TABLE `sleeper` (
  `sid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `accountid` int(11) NOT NULL COMMENT '帐号id',
  `name` varchar(20) COLLATE utf8_unicode_ci NOT NULL COMMENT '姓名',
  `gender` tinyint(4) NOT NULL COMMENT '性别:(1男性/2女性)',
  `birthDay` datetime NOT NULL COMMENT '生日',
  `starSign` tinyint(4) NOT NULL COMMENT '星座',
  `professional` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '职业',
  `weixin` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '微信',
  `email` varchar(30) COLLATE utf8_unicode_ci NOT NULL,
  `telephone` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '电话号码',
  `phoneModel` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '手机型号',
  `bid` int(11) NOT NULL COMMENT '试睡觉民宿id',
  `wantCity` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '向往试睡地',
  `city` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '目前所在地',
  `sleepDate` datetime NOT NULL COMMENT '试睡时间',
  `reason` varchar(1000) COLLATE utf8_unicode_ci NOT NULL COMMENT '试睡理由',
  `level` tinyint(4) NOT NULL COMMENT '试睡等级',
  `sendSms` tinyint(4) NOT NULL COMMENT '短信通知',
  `note` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '备注',
  PRIMARY KEY (`sid`)
) ENGINE=InnoDB AUTO_INCREMENT=4071 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
